WBNB_CONTRACT = '0xbb4cdb9cbd36b01bd1cbaebf2de08d9173bc095c'


# contract hydratator
def getRouterContract():
    return web3.eth.contract(address=uniswap_router, abi=PCSW_ABI)


def print_event(event):
    data = json.loads(Web3.toJSON(event))
    print('TOKEN FOUND')
    token0 = data['args']['token0']
    token1 = data['args']['token1']

    token_contract0 = web3.eth.contract(web3.toChecksumAddress(token0), abi=ERC20_ABI)
    token_contract1 = web3.eth.contract(web3.toChecksumAddress(token1), abi=ERC20_ABI)
    token_symbol0 = token_contract0.functions.symbol().call()
    token_symbol1 = token_contract1.functions.symbol().call()

    print('Token 0 is: '+token_symbol0)
    print('Token 1 is: '+token_symbol1)
    print(json.dumps(data['args'], indent=4, sort_keys=True))


def handle_event(event):
    data = json.loads(Web3.toJSON(event))

    token0 = data['args']['token0']
    token1 = data['args']['token1']

    print('FOUND PAIR')
    if token0 == WBNB_CONTRACT:
        get = token1
    else:
        get = token0

    txn = getRouterContract().functions.swapExactETHForTokensSupportingFeeOnTransferTokens(
        0,
        [web3.toChecksumAddress(WBNB_CONTRACT), web3.toChecksumAddress(get)],
        SENDER_ADDRESS,
        (int(time.time()) + 10000)
    ).buildTransaction({
        'from': SENDER_ADDRESS,
        'value': web3.toWei(BUY_VALUE, 'ether'),  # This is the Token(BNB) amount you want to Swap from
        'gas': GAS_VALUE,
        'gasPrice': GWEI_VALUE,
        'nonce': web3.eth.get_transaction_count(SENDER_ADDRESS),
    })

    txnID = web3.eth.send_raw_transaction(
        web3.eth.account.sign_transaction(txn, private_key=HEXDATA).rawTransaction
    )

    print('Transaction sent 🕐 12 seconds wait')
    time.sleep(12)
    txReceipt = web3.eth.get_transaction_receipt(txnID)

    if txReceipt['status'] == 1:
        print('HEADSHOT 🐃')
    else:
        print('Snipping failed 🐻')

    print('https://bscscan.com/tx/' + web3.toHex(txnID))


def handle_token(event, token):
    data = json.loads(Web3.toJSON(event))

    token0 = data['args']['token0']
    token1 = data['args']['token1']

    print('TOKEN FOUND')
    if token0 == WBNB_CONTRACT:
        get = token1
    else:
        get = token0

    if token != "" and (token0 == token or token1 == token):
        txn = getRouterContract().functions.swapExactETHForTokensSupportingFeeOnTransferTokens(
            0,
            [web3.toChecksumAddress(WBNB_CONTRACT), web3.toChecksumAddress(get)],
            SENDER_ADDRESS,
            (int(time.time()) + 10000)
        ).buildTransaction({
            'from': SENDER_ADDRESS,
            'value': web3.toWei(BUY_VALUE, 'ether'),  # This is the Token(BNB) amount you want to Swap from
            'gas': GAS_VALUE,
            'gasPrice': GWEI_VALUE,
            'nonce': web3.eth.get_transaction_count(SENDER_ADDRESS),
        })

        txnID = web3.eth.send_raw_transaction(
            web3.eth.account.sign_transaction(txn, private_key=HEXDATA).rawTransaction
        )

        print('Transaction sent 🕐 10 seconds wait')
        time.sleep(10)
        txReceipt = web3.eth.get_transaction_receipt(txnID)

        if txReceipt['status'] == 1:
            print('HEADSHOT 🐃')
        else:
            print('Snipping failed 🐻')

        print('https://bscscan.com/tx/' + web3.toHex(txnID))
    else:
        print('Skipping unwanted token')
        print(json.dumps(data, indent=4, sort_keys=True))


def main():
    event_filter = contract.events.PairCreated.createFilter(fromBlock='latest')

    while True:
        for PairCreated in event_filter.get_new_entries():
            handle_event(PairCreated)


def watchToken(token):
    event_filter = contract.events.PairCreated.createFilter(fromBlock='latest')

    while True:
        print('LOOKING FOR ' + str(token))
        for PairCreated in event_filter.get_new_entries():
            handle_token(PairCreated, token)


def watcher():
    event_filter = contract.events.PairCreated.createFilter(fromBlock='latest')

    while True:
        for PairCreated in event_filter.get_new_entries():
            print_event(PairCreated)


if __name__ == "__main__":
    print('Sniper alpha v0a')

    token = ""

    if len(sys.argv) > 1:
        if sys.argv[1] == '-t':
            token = sys.argv[2]
            try:
                watchToken(token)
            except Exception as e:
                watchToken(token)
        elif sys.argv[1] == '-w':
            try:
                watcher()
            except Exception as e:
                watcher()
    else:
        main()
